mapper.select:
select * 
  from member 
 where member_name=#{member_name}
   and member_jumin1=#{member_jumin1}
   and member_jumin2=#{member_jumin2}
   and member_state=1

select * 
  from member
 where member_id=#{member_id}
   and member_name=#{member_name}
   and member_state=1

mapper.update:
update member
   set member_pwd =#{member_pwd}
 where member_id=#{member_id}
   and member_state=1

mapper.select:
select count(*)
  from member
 where member_id=#{member_id}
   and member_name=#{member_name}
   and member_state=1

select * from member
 where member_id=#{login_id} 
   and member_state=1
